        \foreach \i in {1,...,8} {
            \node[circle, fill=yellow!80, draw] (A\i) at ({45*(\i-1)}:2) {};
            \node[circle, fill=red!80, draw] (B\i) at ({45*(\i-1)+90}:2) {};
        }
        % Draw edges within the circle graph
