        % Draw the solid lines for the paths
        \draw[thick] (-7, 3) -- (-5, 1.5);
        \draw[thick] (-5, 1.5) -- (-3, 0.5);
        \draw[thick] (-3, 0.5) -- (0, 0.5);
        \draw[thick] (0, 0.5) -- (2, -0.5);
        \draw[thick] (2, -0.5) -- (4, -1.5);

        % Draw the labels for the paths
        \node at (-6.5, 2.5) {$h_0$};
        \node at (-6.5, 0.75) {$h_0'$};

