    \draw[-latex, line width = 0.5pt] (-2.25cm + 1.5em, {(\i-1)*0.75em + 1em}) -- ++(0.35, 0);

    %Arrows from input to Boxes
    \draw[-latex, line width = 0.5pt] (-3cm , 0 ) -- (-2.5cm ,{(\i-1)*0.75em - 4.75em});
