   }
   \draw[dotted, line width=1.5pt] (-2.3cm,-0em) -- (-2cm,-0em);

 % Define the input node
  \node[draw=black, rectangle, minimum height=5em, minimum width=10em, align=center, left=2.5cm of embedding] (input1) {};

