\begin{tikzpicture}[node distance=1.00cm and 0.5cm,>=latex, rotate=90]
    % Variables
    \node[draw, circle] (x1) {$x_1$};
    \node[draw, circle, right=of x1] (x2) {$x_2$};
    \node[draw, circle, right=of x2] (x3) {$x_3$};
    \node[right=of x3] (xdots1) {$\dots$};
    \node[draw, circle, right=of xdots1] (xi) {$x_i$};
    \node[right=of xi] (xdots) {$\dots$};
    \node[draw, circle, right=of xdots] (xn) {$x_j$};
    \node[draw, circle, right=of xn] (xnn) {$x_n$};

    % Factors
    \node[draw, rectangle, above=of x2] (f1) {$f_1$};
    \node[draw, rectangle, above=of x3] (f2) {$f_2$};
    \node[right=of f2] (fdots1) {$\dots$};
    \node[draw, rectangle, above=of xi] (f3) {$f_{\mu}$};
    \node[right=of f3] (fdots2) {$\dots$};
    \node[draw, rectangle, above=of xn] (fm) {$f_m$};

    % Connections and Messages
    \draw[->] (f1) -- (x1) node[midway, above] {$\mathbf{m_{f_1 \to x_1}}$};
    \draw[->] (xnn) -- (fm) node[midway, above] {$\mathbf{m_{x_n \to f_m}}$};

    % Connections
    \draw[-] (f1) -- (x2);
    \draw[-] (f1) -- (xi);
