   \pgfmathsetmacro\heightoffit{.6*(\y1-\y2)}%
   \resizebox{!}{\heightoffit pt}{\}}%
 };


\draw [-, dashed] (5,{8.5*0.5+0.25}) -- (5,{8.5*0.5}) node[above,yshift=10] {Layering};


\draw [->, dashed] (5,{8.5*0.5}) -- (5.75,{7.5*0.5});

