\begin{tikzpicture}[node distance = 1cm]
    \matrix (grid) [matrix of nodes,
                    row sep=-\pgflinewidth,
                    column sep=-\pgflinewidth,
                    nodes={circle,draw,fill=gray!80, minimum size=5mm}]
    {
        & & & & & & & & & \\
        & & & & & & & & & \\
