    \coordinate (B) at (0,2);

    % Lines
    \draw[dashed] (A) -- (2,2);
    \draw[dotted] (B) -- (2,2);

    % Labels
    \node at (A) [below left] {$(2,0)$};
    \node at (B) [left] {$(0,2)$};
    \node at (2,2) [right] {$(2,2)$};

    % Vector labels
    \node at (1.8,0.2) [below right] {\(\beta^{\text{can}}\)};
