    \draw[->] (-4,0) -- (4,0) node[right] {$v_0$};
    \draw[->] (0,-4) -- (0,4) node[above] {$\bar{v}_0$};

    % Draw the grid
    \foreach \x in {-3,-2,-1,0,1,2,3} {
        \draw (\x,-4) -- (\x,4);
    }
