    % Label points and vectors
    \node at (0,0) [below left] {$O$};
    \node at (2,3) [above right] {Convex Hull};
    \node at (4,-1) {};
    \node at (-30:3) [below right] {$\gamma_\infty$};

    % Add labels for coupling vectors
    \foreach \i in {1,...,5} {
        \pgfmathsetmacro{\angle}{72*\i}
