\centering
\begin{tikzpicture}
  % Nodes for the colored graph
  \node[node style, fill=red] (A) at (0,2) {A};
  \node[node style, fill=blue] (B) at (-1,-1) {B};
  \node[node style, fill=green] (C) at (1,-1) {C};

  % Edges representing the colored graph
  \draw[edge style] (A) -- (B);
  \draw[edge style] (A) -- (C);
