\begin{tikzpicture}[scale=2]
    % Draw the torus
    \draw[thick] (0,0) ellipse (2 and 1);
    \draw[thick] (0,-1) -- (0,1);

    % Draw the fundamental domain
    \draw[thick, fill=gray!20] (-1,-1) rectangle (1,1);

    % Draw the lattice points
