\node[element] (ab) at (0,-4) {ab};
\node[core] (a2) at (-2,-6) {a^2};
\node[core] (b2) at (2,-6) {b^2};

% Edges
\draw[-Stealth] (e1) -- (a);
\draw[-Stealth] (e1) -- (b);
\draw[-Stealth] (a) -- (ab);
