            extra y ticks={2},
            extra y tick labels={$y$},
            every extra x tick/.append style={
                grid=major,
                grid style={dashed,gray!30},
                ticklabel style={fill=white}
            },
            every extra y tick/.append style={
                grid=major,
                grid style={dashed,gray!30},
                ticklabel style={fill=white}
            },
            legend pos=north west,
            width=0.8\textwidth,
            height=0.6\textwidth,
            no markers,
            domain=0:4,
            samples=100,
            smooth
        ]
            \addplot [name path=A, thick] {2*sqrt(x)};
            \addplot [name path=B, thick] {3*sqrt(x)};
            \path [name intersections={of=A and B, by={a,b}}];
            \draw (a) -- (b);
            \node at (axis cs:3.5, 3.5) {$y=G(N')f(k)$};
            \node at (axis cs:3.5, 2.5) {$y=G(N)f(k)$};
        \end{axis}
    \end{tikzpicture}
    \caption{External economies of agglomeration as experienced by a firm through a shift in the production function due to an increase in the size of a city ($N$ to $N'$).}
    \label{fig:external_economies}
