        \draw[->] (16.5,6) -- (16.5,7) node[right] {Lower half of $\mathcal{C}_3$};

        % Caption
        \node at (-2,8) {\small
            \begin{tabular}{l}
                \textbf{$\mathcal{C}_1$} \\
                $\succ_1$ 1 \\
                $\succ_2$ 2 \\
                $\succ_3$ 3 \\
                $\succ_4$ 4 \\
            \end{tabular}
        };
        \node at (-2,12) {\small
            \begin{tabular}{l}
                $\succ_5$ 5 \\
                $\succ_6$ 6 \\
                $\succ_7$ 7 \\
                $\succ_8$ 8 \\
            \end{tabular}
        };
