        \node at (0.5,0.2) {all against 25};

        \draw[dashed, color=black] (0,0) -- (1,0);
        \node at (0.5,0.2) {excluded in level 2};

        \draw[dotted, color=black] (0,0) -- (1,0);
        \node at (0.5,0.2) {excluded in level 4};

        \draw[dashdotted, color=black] (0,0) -- (1,0);
        \node at (0.5,0.2) {excluded in level 6};

