        % Draw the horizontal lines
        \draw[thick, red] (0,1) -- (4,1);
        \draw[thick, blue] (4,1) -- (6,1);
        \draw[thick, green] (6,1) -- (8,1);

        % Draw the vertical lines
        \draw[dashed] (0,1) -- (0,0) node[left] {$0$};
        \draw[dashed] (4,1) -- (4,0) node[below] {$\frac{\ell}{2}$};
        \draw[dashed] (6,1) -- (6,0) node[below] {$\frac{\ell}{2} + 2\ell$};
        \draw[dashed] (8,1) -- (8,0) node[below] {$\ell$};

        % Draw the labels for the cutoff functions
        \node at (1,1.3) [above] {$\eta$};
        \node at (5,1.3) [above] {$\tilde{\eta}$};
        \node at (7,1.3) [above] {$1 - \tilde{\eta}$};

        % Draw the curves
        \draw[thick, red, domain=0:4] plot (\x, {1 - (\x-4)^2/4});
        \draw[thick, purple, domain=4:6] plot (\x, {(x-4)^2/4});
        \draw[thick, green, domain=6:8] plot (\x, {(x-6)^2/4});
    \end{tikzpicture}
    \caption{The cutoffs $\eta, \tilde{\eta} \in C_c^\infty(\mathbb{R}^d)$ in velocity that are used in the proof of Proposition \ref{prop:L2-Linfty-A}.}
    \label{fig:cutoffs}
