    \def\coldist{1.5}

    % Draw the grid lines
    \draw[dashed] (0,0) grid (9*\coldist, 5*\rowdist);

    % Label the rows
    \foreach \y [count=\i from 0] in {0,...,4} {
        \node at (-0.5*\coldist, \i*\rowdist + 0.5*\rowdist) {\i};
    }

