        \node at (\i*\coldist - 0.5*\coldist, -0.5*\rowdist) {\i};
    }

    % Place the vertices
    \foreach \x in {0,...,8} {
        \foreach \y in {0,...,4} {
            \pgfmathtruncatemacro{\color}{mod(\x+\y, 10)}
