	},
	declare function={
		sig5(\x)= 1;
	},
	declare function={
		sig6(\x)= 2.5;
	},
	declare function={
		sig7(\x)= 1.5;
	},
	declare function={
		sig8(\x)= 2.25;
	},
	declare function={
		gamma(\x)= (\x < 1) * (2.75) +
		and(1 <= \x, \x < 3) * (2)  +
		and(3 <= \x, \x < 4) * (1.75)  +
		(4 <= \x) * (1)
	;
	},
