            \coordinate (C) at (0,3);
            \coordinate (D) at (0,-3);

            \draw[thick] (A) -- (B) -- (C) -- (D) -- cycle;
            \fill[yellow!50] (A) -- (B) -- (C) -- cycle;
            \draw[dashed] (A) -- (C);
            \draw[dashed] (B) -- (D);
            \node at (0,-1) {$(2)$};
        \end{scope}

        \begin{scope}[xshift=6cm,yshift=-6cm]
            \coordinate (A) at (-3,0);
            \coordinate (B) at (3,0);
            \coordinate (C) at (0,3);
            \coordinate (D) at (0,-3);

            \draw[thick] (A) -- (B) -- (C) -- (D) -- cycle;
            \fill[yellow!50] (A) -- (B) -- (C) -- cycle;
            \draw[dashed] (A) -- (C);
            \draw[dashed] (B) -- (D);
            \node at (0,-1) {$(2^{+})$};
        \end{scope}
    \end{tikzpicture}

    \caption{Region $M^{\rm I}(i)$ of possible $t$, for $i=\bullet$ in regions labeled by $-1^*$, $1^*$, $2^*$.}
