\draw[red, dashed] (5,1.5) -- (5,0) node[right] {};
\draw[orange, dashed] (0.5,2.625) -- (0.5,0) node[right] {};

% Initial guess
\filldraw[fill= black, draw=black] (5,1.5) circle (2pt) node[right] {$(x_0, f(x_0))$};
