    \draw[->, dashed] (m-1-1.south west) -- ++(-0.5,0) node[left] {Conversion};

    % Draw the braces
    \draw[thick, -Stealth] (m-1-1.north east) -- ++(0.5,0) |- (m-1-1.south east);
