        \node at (3,2) {$h_{NM}$};
        \node at (3,3) {$h_{MM}$};

        % Add the labels for the treatment status
        \node at (-1,1.5) {$h_{ij}=1$};
        \node at (-1,0.5) {$h_{ij}=0$};

        % Add the legend
