\pgfplotsset{compat=1.18}

\begin{document}

\begin{figure}[h]
    \centering
    \begin{tikzpicture}
        \begin{axis}[
            xlabel=$\alpha$,
            ylabel=Worst group accuracy,
            legend pos=south east,
            grid=major,
            ymin=60,
            ymax=90,
            xmin=0,
            xmax=10,
            ytick={60,70,...,90},
