\documentclass{article}
\usepackage{tikz}
\usepackage{pgfplots}
\pgfplotsset{compat=1.17}

\begin{document}

\begin{tikzpicture}
    \begin{axis}[
        axis lines=middle,
        xlabel=$x \ln x$,
        ylabel={(1+$a)x \ln x$},
        xmin=0, xmax=20,
        ymin=-5, ymax=5,
        xtick=\empty,
        ytick=\empty,
        no markers,
        domain=0:20,
        samples=100,
        every axis plot post/.append style={ultra thick},
        clip=false
    ]

    % Plot the black curve (1 + 2a)x ln(x)
