        % EfficientNet-b5
        \node[anchor=south west, inner sep=0] at (rel axis cs:0.6,0.6) {EfficientNet-b5};

        % EfficientNet-b0
        \node[anchor=south west, inner sep=0] at (rel axis cs:0.7,0.7) {EfficientNet-b0};

        % ShuffleNetV2
        \node[anchor=south west, inner sep=0] at (rel axis cs:0.8,0.8) {ShuffleNetV2};

        % Ours
        \node[anchor=south west, inner sep=0] at (rel axis cs:0.9,0.9) {Ours};

        % EfficientFormer
        \node[anchor=south west, inner sep=0] at (rel axis cs:0.4,0.4) {EfficientFormer};

        % Red Arrow
        \draw[->, red, thick] (rel axis cs:0.7,0.7) -- (rel axis cs:0.9,0.9) node[midway, above, red] {Better};

    \end{axis}
    \end{tikzpicture}
    \caption{Top-1 accuracy, inference speed and model size on the test set. The size of the circle denotes the model size, the smaller it is the fewer the model parameters. The closer the model to top right corner represents higher accuracy and faster inference. As the red arrow shows, it can be perceived as toward better performance.}
    \label{fig:performance}
\end{figure}

