    \filldraw[fill=blue!30] (2,-0.75) rectangle (3.5,0.75);
    \node at (2.75,0) {$M$};
    % Mass clump
    \filldraw[fill=blue!30] (4.5,-0.25) rectangle (5.0,0.25);
    \node at (4.75,0) {$m$};
    % Wall: Centre of NS
    \draw[thick] (0,1.75) -- (0,-1.5);
    \foreach \y in {-1,-0.5,0,0.5,1,1.5} {
        \draw[thick] (0,\y) -- (-0.25,\y-0.25);
    }
